// Import our Config file
const config = require('../config.json');

//Import MySQL library
const mysql = require('mysql');

//Create the MySQL connection pool
//createPool function used so the server doesn't time out connections and crash out app. Also allows more than one connection at a time.
const connection = mysql.createPool({
    connectionLimit: 50,
    host: config.mysqlhost, //host from config
    user: config.mysqluser, //user from config
    password: config.mysqlpass, //password from config
    database: config.mysqldatabase, //database from config
    charset: 'utf8mb4' // Have also tried utf8, utf8_general_ci, etc.
});

//Initialize empty Database object
Database = {};


Database.addUser = function (uData, cb){
    connection.getConnection(function (err, conn) {
        conn.query('INSERT INTO website_users (uuid, username, role, discord_username, discord_id, application, approved_by, denied_by, hackcheck) VALUES (?,?,?,?,?,?,?,?,?)', [uData.uuid, uData.username, uData.role, uData.discord_name, uData.discord_id, uData.application, 0, 0, 0, 0], function (err, bRows) {
            if (err) throw err;
            if (bRows.rowsAffected < 1) {
                cb(false);
            } else {
                cb(true);
            }
        });
    })
};

