Database.checkStats = function (data, cb) {
    connection.getConnection(function (err, conn){
        conn.query('SELECT uuid FROM stats_broken WHERE uuid = ?', [data], function (err, dRows) {
            if (dRows.length > 1) {
                //send false back to the first function using the original callback cb, our result is empty or error
                cb(false);
            } else {
                cb(true)
            }
        });
        conn.release();
    });
};

Database.statsInit = function (iStats, uData, cb) {
    connection.getConnection(function (err, conn){
        conn.query('INSERT INTO stats_broken (uuid, username) VALUES (?,?)', [uData.uuid, uData.username], function (err, bRows) {
            if (err) throw err;
            if (bRows.rowsAffected > 1) {
                cb(false);
            } else {
                conn.query('INSERT INTO stats_crafted (uuid, username) VALUES (?,?)', [uData.uuid, uData.username], function (err, cRows) {
                    if (err) throw err;
                    if (cRows.rowsAffected > 1) {
                        cb(false);
                    } else {
                        conn.query('INSERT INTO stats_custom (uuid, username) VALUES (?,?)', [uData.uuid, uData.username], function (err, cuRows) {
                            if (err) throw err;
                            if (cuRows.rowsAffected> 1) {
                                cb(false);
                            } else {
                                conn.query('INSERT INTO stats_killed (uuid, username) VALUES (?,?)', [uData.uuid, uData.username], function (err, kRows) {
                                    if (err) throw err;
                                    if (kRows.rowsAffected > 1) {
                                        cb(false);
                                    } else {
                                        conn.query('INSERT INTO stats_killedby (uuid, username) VALUES (?,?)', [uData.uuid, uData.username], function (err, kbRows) {
                                            if (err) throw err;
                                            if (kbRows.rowsAffected > 1) {
                                                cb(false);
                                            } else {
                                                conn.query('INSERT INTO stats_mined (uuid, username) VALUES (?,?)', [uData.uuid, uData.username], function (err, mRows) {
                                                    if (err) throw err;
                                                    if (mRows.rowsAffected > 1) {
                                                        cb(false);
                                                    } else {
                                                        conn.query('INSERT INTO stats_pickedup (uuid, username) VALUES (?,?)', [uData.uuid, uData.username], function (err, pRows) {
                                                            if (err) throw err;
                                                            if (pRows.rowsAffected > 1) {
                                                                cb(false);
                                                            } else {
                                                                conn.query('INSERT INTO stats_used (uuid, username) VALUES (?,?)', [uData.uuid, uData.username], function (err, sRows) {
                                                                    if (err) throw err;
                                                                    if (sRows.rowsAffected > 1) {
                                                                        cb(false);
                                                                    } else {
                                                                        cb(true);
                                                                    }
                                                                });
                                                            }
                                                        });
                                                    }
                                                });
                                            }
                                        });
                                    }
                                });
                            }
                        });
                    }
                });
            }
        });
        conn.release();
    });
};

