Database.getEnderkills = function (cb) {
    connection.getConnection(function (err, conn){
        conn.query('SELECT SUM(ender_dragon) FROM stats_killed', function (err, dRows) {
            if (!dRows[0]['SUM(ender_dragon)']) {
                cb(false, '0');
            } else {
                cb(true, dRows[0]['SUM(ender_dragon)'])
            }
        });
        conn.release();
    });
};
